		fDocs.put("pki:template-get-request","<div><div style=\"font-family:Courier;\"><div style=\"color:#c00831;\">pki:template-get-request(</div><div style=\"padding-left:2em;\">template as element(pki:template)</div><div>)</div></div><div style=\"margin-top:1em;\"><apidoc:summary xmlns:apidoc=\"http://marklogic.com/xdmp/apidoc\">This function returns the request portion of the certificate template. </apidoc:summary></div><div style=\"margin-top:1em;\"><a style=\"font-size:.8em\" href=\"mlapidoc:pki:template-get-request\">Complete Information for pki:template-get-request</a></div></div>");
